mapper.update:
UPDATE MYCAR
SET
CAR_NO = #{car_no},
CAR_KINDS = #{car_kinds},
CAR_DATE = #{car_date},
CAR_OIL = #{car_oil}
WHERE MEM_NO = #{mem_no}
AND CAR_NO = #{car_no}

UPDATE MYCAR
SET
CAR_NO = #{car_no1}
WHERE CAR_NO = #{car_no}

mapper.select:
SELECT *
FROM MYCAR
WHERE MEM_NO = #{mem_no}
AND CAR_NO = #{car_no}

mapper.delete:
DELETE FROM MYCAR
WHERE MEM_NO = #{mem_no}
AND CAR_NO = #{car_no}

mapper.select:
SELECT CAR_NO, MD_DATE, MD_KM
FROM MYCAR_DETAIL
WHERE CAR_NO = #{car_no}

SELECT CAR_NO, MD_DATE, MD_KM, MD_BREAK
FROM MYCAR_DETAIL
WHERE MEM_NO = #{mem_no}
AND CAR_NO = #{car_no}

SELECT CAR_NO, MD_DATE, MD_KM, MD_ENGINE
FROM MYCAR_DETAIL
WHERE MEM_NO = #{mem_no}
AND CAR_NO = #{car_no}
