mapper.insert:
INSERT INTO CATEGORY(CATEGORY_NO,
					MEM_NO,
					CT_NAME
					)
					values(FRIEND_SEQ.NEXTVAL,
					#{mem_no},
					#{ct_name}
					)

mapper.select:
SELECT CATEGORY_NO
FROM CATEGORY
WHERE MEM_NO = #{mem_no}

mapper.update:
UPDATE CATEGORY
SET
CT_NAME = #{ct_name}
WHERE MEM_NO = #{mem_no}
