mapper.select:
SELECT *
FROM PARTICIPATION

WHERE CH_NO = #{ch_no}


WHERE MEM_NO = #{mem_no}

SELECT * FROM MEMBER WHERE MEM_NO != #{mem_no}
AND MEM_ID LIKE '%'||#{mem_id}||'%'
AND MEM_NAME LIKE '%'||#{mem_name}||'%'

		select a.MEM_NO MEM_NO, b.MEM_NO FRI_MEM_NO, a.MEM_NAME MEM_NAME, b.MEM_NAME FRI_MEM_NAME, a.MEM_ID MEM_ID, b.MEM_ID FRI_MEM_ID
				from (
    				select *
    				from MEMBER
    				where mem_no = #{mem_no}
				) a,
				(select *
    			from MEMBER
    			where mem_no = #{fri_mem_no}
    			) b

SELECT *
FROM CHAT_FILE
WHERE MEM_NO = #{mem_no}
AND CH_NO = #{ch_no}
