mapper.insert:
INSERT INTO
FileItem(
FILE_NO,FILE_NAME,FILE_SAVE_NAME,
FILE_TYPE,FILE_SIZE, K_NO
)

VALUES(
FILE_SEQ.NEXTVAL,#{file_name},#{file_save_name},
#{file_type},#{file_size}, #{k_no}
)

mapper.update:
update FileItem
set FILE_NAME = ${file_name}, FILE_SAVE_NAME = ${file_save_name}, FILE_TYPE = ${file_type}, FILE_SIZE = ${file_size}
where K_NO = ${k_no}
