mapper.insert:
		INSERT INTO
		INBODY(
		FILE_NO,FILE_NAME,FILE_SAVE_NAME,
		FILE_TYPE,FILE_SIZE,
		FILE_INBODY_NO
		)

		VALUES(
		INBODY_SEQ.NEXTVAL,#{file_name},#{file_save_name},
		#{file_type},#{file_size}, #{file_inbody_no}
		)

mapper.update:
UPDATE INBODY
SET FILE_NAME = #{file_name}, FILE_SAVE_NAME = #{file_save_name},
FILE_TYPE = #{file_type}, FILE_SIZE = #{file_size}
WHERE FILE_INBODY_NO = #{file_inbody_no}
