mapper.insert:
    		
    			SELECT KNOWLEDGE_SEQ.NEXTVAL FROM DUAL
    		
    		INSERT INTO KNOWLEDGE(K_NO
									,K_TITLE    
									,K_CONTENT
									,K_ANSWER1
									,K_ANSWER2
									,K_ANSWER3
									,K_ANSWER4
									,K_ANSWER
									,MEM_NO
									)
							VALUES(#{k_no}
									,#{k_title} 
									,#{k_content}  
									,#{k_answer1}
									,#{k_answer2} 
									,#{k_answer3}
									,#{k_answer4}
									,#{k_answer}
									,1
									)
