mapper.update:
UPDATE MEMBER
SET
MEM_NICKNAME = #{mem_nickname}
,MEM_ZIP1 = #{mem_zip1}
,MEM_ZIP2 = #{mem_zip2}
,MEM_ADDR1 = #{mem_addr1}
,MEM_ADDR2 =
#{mem_addr2}
,MEM_HP = #{mem_hp}
,MEM_EMAIL = #{mem_email}

	,MEM_PASS = #{mem_pass}


	,MEM_NAME = #{mem_name}


	,MEM_BIRTH = #{mem_birth}

WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT
MEM_ID
FROM MEMBER
WHERE MEM_NAME = #{mem_name}
AND MEM_BIRTH =
#{mem_birth}

SELECT
MEM_PASS
FROM MEMBER
WHERE MEM_NAME = #{mem_name}
AND MEM_BIRTH =
#{mem_birth}
AND MEM_ID = #{mem_id}
AND MEM_hp = #{mem_hp}

mapper.update:
UPDATE MEMBER
SET
MEM_TEMPORARY_PASS = #{mem_temporary_pass}
WHERE MEM_ID = #{mem_id}

UPDATE MEMBER
SET MEM_PASS
= #{mem_pass}
WHERE MEM_ID = #{mem_id}
