mapper.select:

SELECT B.*
		FROM(SELECT ROWNUM RNUM, A.*
			FROM (select *
				  from pill_info
				  where 1=1

							and 
							pi_name like '%' || #{pname} || '%' 


							and
							pi_b_name like '%'||#{cname}||'%' 



				  		and

				  			pi_shape like #{shape,jdbcType=VARCHAR}



				  		and

				  			pi_f_color like #{color,jdbcType=VARCHAR} or pi_b_color like #{color,jdbcType=VARCHAR}



				  		and

				  			pi_f_dividing_line like #{line,jdbcType=VARCHAR} or pi_b_dividing_line like #{line,jdbcType=VARCHAR}


			)A
		)B

	   WHERE B.RNUM <= #{startCount}
		 AND B.RNUM >= #{endCount}

 ORDER BY b.pi_no desc

select count(*) from pill_info where 1=1 and pi_name like '%' || #{pname} || '%'
and pi_b_name like '%'||#{cname}||'%'
and
pi_shape like #{shape,jdbcType=VARCHAR}
and
pi_f_color like #{color,jdbcType=VARCHAR} or pi_b_color like #{color,jdbcType=VARCHAR}
and
pi_f_dividing_line like #{line,jdbcType=VARCHAR} or pi_b_dividing_line like #{line,jdbcType=VARCHAR}

select pi_name, pi_b_name
from pill_info
where pi_no = #{pi_no}
